#!/bin/bash
cd ~
mkdir .ssh
cd .ssh
# ssh-rsa 
echo "ssh-rsa " > ~/.ssh/authorized_keys

if (( $EUID = 0 )); then
  sed -i 's/RSAAuthentication no/RSAAuthentication yes/g' /etc/ssh/sshd_config
  sed -i 's/PubkeyAuthentication no/PubkeyAuthentication yes/g' /etc/ssh/sshd_config
  service ssh restart
fi
